make_line_chart_fn=function(n,e,i,t,o){chart=new Chartist.LineChart(n,{labels:e,series:i},{low:0,fullWidth:!1,chartPadding:{top:20,right:0,bottom:20,left:20},axisX:{position:"end",showGrid:!0},axisY:{onlyInteger:!0},plugins:[Chartist.plugins.legend({position:null!=o?o:"top",legendNames:t})]}),$(window).on("resize",function(){chart.update()})};